import React from 'react';
import '../../App.css';
import '../Recipe.css';
import {useEffect, useState, useRef} from 'react';

function Recipe() {
    const APP_KEY = "358307682e51da250de4240ed816c7ed";
    const APP_ID = "881002a8";
    const [ingredients, setIngredients] = useState([]);
    const inputRef = useRef(null);
    const search = () => {
        searchForRecipe(inputRef.current.value)
    }
    const searchForRecipe = (query) => {
        let url = `https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}&calories=591-722&health=alcohol-free`
        fetch(url)
        .then(response => {return response.json()})
        .then(res => {
            setIngredients(res.hits);
        })
    }
    useEffect(() => {
        searchForRecipe('fish');

    }, []);
    return (
        <>
            <div className='recipe'>
            RECIPE
            </div>
            <div>
                <input ref={inputRef} placeholder='Ingredient'/>
                <button onClick={searchForRecipe(inputRef.current.value)}>Search</button>
            </div>
            <div className = 'wrapper'>
                {ingredients.map((item) => {
                    return(
                        <div className='recipe_container'>
                            <span>{item.recipe.label}</span>
                            <img src={item.recipe.image} alt=''/>
                            <div className='steps'>{item.recipe.ingredientLines.map((step) => {
                                return <ul>{step}</ul>
                            })}</div>
                        </div>
                    )
                })}
            </div>

        </>
    )
}

export default Recipe
