import React from 'react';
import '../../App.css';
import '../Recipe.css';
import {useEffect, useState} from 'react';

function Recipe() {
    const APP_KEY = "358307682e51da250de4240ed816c7ed";
    const APP_ID = "881002a8";
    const [ingredients, setIngredients] = useState([]);
    useEffect(() => {
        let url = `https://api.edamam.com/search?q=fish&app_id=${APP_ID}&app_key=${APP_KEY}&from=0&to=3&calories=591-722&health=alcohol-free`
        fetch(url)
        .then(response => {return response.json()})
        .then(res => {
            setIngredients(res.hits);
            console.log(res);
        })

    }, []);
    return (
        <>
            <div className='recipe'>
            RECIPE
            </div>
            <div className = 'wrapper'>
                {ingredients.map((item) => {
                    return(
                        <div className='recipe_container'>
                        <span>{item.recipe.label}</span>
                        <img src={item.recipe.image} alt=''/>
                        <p>{item.recipe.ingredientLines.map((step) => {
                            return <ul>{step}</ul>
                        })}</p>
                        </div>
                    )
                })}
            </div>

        </>
    )
}

export default Recipe
